import { createClient } from '@supabase/supabase-js';

// Your Supabase configuration
const supabaseUrl = process.env.REACT_APP_SUPABASE_URL || 'https://lgoqhgnlhjrokwbnkmbc.supabase.co';
const supabaseKey = process.env.REACT_APP_SUPABASE_ANON_KEY || 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Imxnb3FoZ25saGpyb2t3Ym5rbWJjIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTExOTQzODAsImV4cCI6MjA2Njc3MDM4MH0.CIdrHuTiLMzfestzpGhSVStjH5DFWgEiOrl93o5Jatc';

// Create Supabase client with debug logging
const supabase = createClient(supabaseUrl, supabaseKey, {
  auth: {
    debug: true,
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: true
  }
});

export { supabase };
export default supabase;
