import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Auth = () => {
  const navigate = useNavigate();
  const { 
    signUpWithPhone, 
    signUpWithEmail, 
    signInWithPhone, 
    signInWithEmail, 
    verifyOTP, 
    authError, 
    clearError 
  } = useAuth();

  const [isSignUp, setIsSignUp] = useState(true);
  const [authMethod, setAuthMethod] = useState('phone'); // 'phone' or 'email'
  const [identifier, setIdentifier] = useState('');
  const [otp, setOtp] = useState('');
  const [showOtpInput, setShowOtpInput] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    clearError();

    if (!showOtpInput) {
      // Send OTP
      let result;
      if (isSignUp) {
        if (authMethod === 'phone') {
          result = await signUpWithPhone(identifier);
        } else {
          result = await signUpWithEmail(identifier);
        }
      } else {
        if (authMethod === 'phone') {
          result = await signInWithPhone(identifier);
        } else {
          result = await signInWithEmail(identifier);
        }
      }

      if (result.success) {
        setMessage(result.message);
        setShowOtpInput(true);
      } else {
        setMessage(result.error);
      }
    } else {
      // Verify OTP
      const result = await verifyOTP(identifier, otp);
      if (result.success) {
        setMessage('Authentication successful!');
        setTimeout(() => {
          navigate('/');
        }, 1000);
      } else {
        setMessage(result.error);
      }
    }

    setLoading(false);
  };

  const handleResendOTP = async () => {
    setLoading(true);
    setMessage('');
    clearError();

    let result;
    if (isSignUp) {
      if (authMethod === 'phone') {
        result = await signUpWithPhone(identifier);
      } else {
        result = await signUpWithEmail(identifier);
      }
    } else {
      if (authMethod === 'phone') {
        result = await signInWithPhone(identifier);
      } else {
        result = await signInWithEmail(identifier);
      }
    }

    if (result.success) {
      setMessage('OTP resent successfully!');
    } else {
      setMessage(result.error);
    }

    setLoading(false);
  };

  const resetForm = () => {
    setIdentifier('');
    setOtp('');
    setShowOtpInput(false);
    setMessage('');
    clearError();
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900 dark:text-white">
            {isSignUp ? 'Create your account' : 'Sign in to your account'}
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600 dark:text-gray-400">
            {isSignUp ? 'Already have an account?' : "Don't have an account?"}{' '}
            <button
              onClick={() => {
                setIsSignUp(!isSignUp);
                resetForm();
              }}
              className="font-medium text-red-600 hover:text-red-500 dark:text-red-400 dark:hover:text-red-300"
            >
              {isSignUp ? 'Sign in' : 'Sign up'}
            </button>
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 py-8 px-6 shadow rounded-lg">
          {/* Auth Method Toggle */}
          <div className="flex rounded-md shadow-sm mb-6">
            <button
              type="button"
              onClick={() => {
                setAuthMethod('phone');
                resetForm();
              }}
              className={`flex-1 py-2 px-4 text-sm font-medium rounded-l-md border ${
                authMethod === 'phone'
                  ? 'bg-red-600 text-white border-red-600'
                  : 'bg-white dark:bg-gray-700 text-gray-700 dark:text-gray-300 border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-600'
              }`}
            >
              Phone
            </button>
            <button
              type="button"
              onClick={() => {
                setAuthMethod('email');
                resetForm();
              }}
              className={`flex-1 py-2 px-4 text-sm font-medium rounded-r-md border-t border-r border-b ${
                authMethod === 'email'
                  ? 'bg-red-600 text-white border-red-600'
                  : 'bg-white dark:bg-gray-700 text-gray-700 dark:text-gray-300 border-gray-300 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-600'
              }`}
            >
              Email
            </button>
          </div>

          <form className="space-y-6" onSubmit={handleSubmit}>
            {/* Identifier Input */}
            {!showOtpInput && (
              <div>
                <label htmlFor="identifier" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  {authMethod === 'phone' ? 'Phone Number' : 'Email Address'}
                </label>
                <div className="mt-1">
                  <input
                    id="identifier"
                    name="identifier"
                    type={authMethod === 'phone' ? 'tel' : 'email'}
                    required
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    placeholder={authMethod === 'phone' ? '+1234567890' : 'user@example.com'}
                    className="appearance-none relative block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 placeholder-gray-500 dark:placeholder-gray-400 text-gray-900 dark:text-white rounded-md focus:outline-none focus:ring-red-500 focus:border-red-500 focus:z-10 sm:text-sm bg-white dark:bg-gray-700"
                  />
                </div>
              </div>
            )}

            {/* OTP Input */}
            {showOtpInput && (
              <div>
                <label htmlFor="otp" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                  Enter OTP
                </label>
                <div className="mt-1">
                  <input
                    id="otp"
                    name="otp"
                    type="text"
                    required
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    placeholder="Enter 6-digit code"
                    maxLength="6"
                    className="appearance-none relative block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 placeholder-gray-500 dark:placeholder-gray-400 text-gray-900 dark:text-white rounded-md focus:outline-none focus:ring-red-500 focus:border-red-500 focus:z-10 sm:text-sm bg-white dark:bg-gray-700"
                  />
                </div>
                <div className="mt-2 flex justify-between items-center">
                  <button
                    type="button"
                    onClick={handleResendOTP}
                    disabled={loading}
                    className="text-sm text-red-600 hover:text-red-500 dark:text-red-400 dark:hover:text-red-300 disabled:opacity-50"
                  >
                    Resend OTP
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowOtpInput(false)}
                    className="text-sm text-gray-600 hover:text-gray-500 dark:text-gray-400 dark:hover:text-gray-300"
                  >
                    Change {authMethod === 'phone' ? 'phone' : 'email'}
                  </button>
                </div>
              </div>
            )}

            {/* Error/Success Message */}
            {(authError || message) && (
              <div className={`rounded-md p-4 ${
                authError ? 'bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800' : 'bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800'
              }`}>
                <p className={`text-sm ${
                  authError ? 'text-red-800 dark:text-red-200' : 'text-green-800 dark:text-green-200'
                }`}>
                  {authError || message}
                </p>
              </div>
            )}

            {/* Submit Button */}
            <div>
              <button
                type="submit"
                disabled={loading || (!showOtpInput && !identifier) || (showOtpInput && !otp)}
                className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                ) : (
                  showOtpInput ? 'Verify OTP' : `Send OTP to ${authMethod === 'phone' ? 'Phone' : 'Email'}`
                )}
              </button>
            </div>
          </form>

          {/* Demo Info */}
          <div className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-md">
            <h3 className="text-sm font-medium text-blue-800 dark:text-blue-200 mb-2">
              Demo Information
            </h3>
            <p className="text-xs text-blue-700 dark:text-blue-300">
              For testing purposes, you can use any valid phone number or email address. 
              Supabase will send a real OTP to the provided contact information.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Auth;
